import React, {useState} from "react";

export default function DetailsGenerator(){

    const [id,setId] = useState(0);
    const [year,setYear] = useState(0);
    const [month,setMonth] = useState("");
    const [day,setDay] = useState(0);
    const [gender,setGender] = useState("");



    const generator = () => {
        let number = Number(id.slice(0, -5));
        let year = (number > 100) ? number/1000 : 1900+(number/1000);
        setYear(year);
        let number1 = number % 1000;
        if ((number1 > 0) && (number1 < 367)){
            setGender("Male");
            switch(true) {
                case (number1 < 32):
                  setMonth("Jan");
                  setDay(number1);
                  break;
                case (number1 < 61):
                    setMonth("Feb");
                    setDay(number1 - 31);
                  break;
                case (number1 < 92):
                  setMonth("Mar");
                  setDay(number1 - 60);
                  break;
                case (number1 < 122):
                    setMonth("Apr");
                    setDay(number1 - 91);
                  break;
                case (number1 < 153):
                  setMonth("May");
                  setDay(number1 - 121);
                  break;
                case (number1 < 183):
                    setMonth("Jun");
                    setDay(number1 - 152);
                  break;
                case (number1 < 214):
                  setMonth("Jul");
                  setDay(number1 - 182);
                  break;
                case (number1 < 245):
                  setMonth("Aug");
                  setDay(number1 - 213);
                  break;
                case (number1 < 275):
                  setMonth("Sep");
                  setDay(number1 - 244);
                  break;
                case (number1 < 306):
                  setMonth("Oct");
                  break;
                case (number1 < 336):
                  setMonth("Nov");
                  break;
                default:
                  setMonth("Dec");
              }
        }
        else if ((number1 > 500) && (number1 < 867)){
            setGender("Female");
        }
        else {
            alert("Invalid ID number");
        }
    }

    return(
        <div>
            <h1>Enter your NIC number here</h1>
            <label>NIC:</label>
            <input type="text" onChange={(e)=>{
                    setId(e.target.value);
                }}></input>
            <button onClick={generator}>Sumbit</button>
        </div>
    )
}
